			"content": {
				"title": {
					"arabic": "كلمات التحية الأساسية",
					"english": "Basic Greeting Words",
					"transliteration": "Kalemat el taheya el asaseya"
				},
				"phrases": [
					{
						"arabic": "أهلاً",
